import { useEffect, useState } from 'react';
import classNames from 'classnames/bind';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleXmark, faSpinner, faMagnifyingGlass, faEllipsisVertical, faGlobe, faCircleQuestion, faKeyboard } from '@fortawesome/free-solid-svg-icons';
import { faPlusSquare } from '@fortawesome/free-regular-svg-icons';


import Tippy from '@tippyjs/react/headless';

import Button from '~/components/Button';
import styles from './Header.module.scss';
import images from '~/assets/images'
import { Wrapper as PropperWrapper } from '~/components/Propper';
import AccountItem from '~/components/AccountItem';
import Menu from '~/components/Propper/MenuItem';

const cx = classNames.bind(styles)
const MENU_ITEMS = [
  {
    icon: <FontAwesomeIcon icon={faGlobe} />,
    title: 'English',
    subChildren: {
      title: 'Language',
      data: [
        {
          code: 'en',
          title: 'English'
        },
        {
          code: 'en',
          title: 'English'
        }
      ]
    }
  },
  {
    icon: <FontAwesomeIcon icon={faCircleQuestion} />,
    title: 'Feedback and help',
    to: '/feedback'
  },
  {
    icon: <FontAwesomeIcon icon={faKeyboard} />,
    title: 'Keyboard shortcuts',
  }
]
function Header() {
  const [searchResult, setSearchResult] = useState([])

  useEffect(() => {
    setInterval(() => {
      setTimeout(() => {
        setSearchResult([])
      }, 0)
    })
  }, [])
  return <header className={cx('wrapper')}>
    <div className={cx('inner')}>
      <img src={images.logo} alt="Tiktok" />
      <Tippy
        visible={searchResult.length > 0}
        interactive
        render={attrs => (
          <div className={cx('search-results')} tabIndex="-1">
            <PropperWrapper>
              <h4 className={cx('search-title')}>
                Accounts
              </h4>
              <AccountItem />
              <AccountItem />
              <AccountItem />
              <AccountItem />
            </PropperWrapper>
          </div>
        )}
      >
        <div className={cx('search')}>
          <input type="text" placeholder="Search accounts and videos" spellCheck={false} />

          {/*Clear*/}
          <button className={cx('clear')}>
            <FontAwesomeIcon icon={faCircleXmark} />
          </button>

          {/*Loading*/}
          <FontAwesomeIcon className={cx('loading')} icon={faSpinner} />

          {/*Search button*/}

          <button className={cx('search-btn')}>
            <FontAwesomeIcon icon={faMagnifyingGlass} />
          </button>

        </div>
      </Tippy>
      <div className={cx('actions')}>
        <Button text leftIcon={<FontAwesomeIcon icon={faPlusSquare} />}>
          Upload
        </Button>
        <Button primary>Log in</Button>
        <Menu items={MENU_ITEMS}>
          <button className={cx('more-btn')}>
            <FontAwesomeIcon icon={faEllipsisVertical}></FontAwesomeIcon>
          </button>
        </Menu>
      </div>
    </div>
  </header>
}

export default Header;
