import classNames from "classnames/bind";
import { Link } from 'react-router-dom'
import styles from "./Button.module.scss"

const cx = classNames.bind(styles)

function Button({
  to, 
  href, 
  primary = false, 
  outline = false, 
  small = false, 
  large = false, 
  text = false, 
  disabled = false,
  rounded = false,
  children, 
  className,
  leftIcon,
  onClick, 
  ...passProps
  }) {

  let Comp = 'button'

  const props = {
    onClick,
    ...passProps
  }

  //Remove eventListener
  if(disabled) {
    Object.keys(props).forEach(key => {
      if(key.startsWith('on') && typeof props[key] === 'function') {
        delete props[key]
      }
    })
  }

  if (to) {
    props.to = to
    Comp = Link
  } else if (href) {
    props.href = href
    Comp = 'a'
  }

  const classes = cx('wrapper', {
    [className]: className,
    primary,
    outline,
    small,
    large,
    text,
    disabled,
    rounded,
  })

  return (
    <Comp className={classes} {...props}>
      {leftIcon && <span className={icon}></span>}
      <span>{children}</span>
    </Comp>
  );

}

export default Button;
